// import logo from './logo.svg';
import './App.css';
import "leaflet/dist/leaflet.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { useState } from "react";
import axios from 'axios';
import { Icon, divIcon, point } from 'leaflet';
import MarkerClusterGroup from "react-leaflet-cluster";
// import { useState } from 'react';

function App() {
  // Markers
  const markers = [
    {
      geocode: [48.86, 2.3522],
      popup: "Hello, I am popup 1"
    },
    {
      geocode: [48.85, 2.3522],
      popup: "Hello, I am popup 2"
    },
    {
      geocode: [48.855, 2.34],
      popup: "Hello, I am popup 3"
    }
  ]

  const customIcon = new Icon ({
    iconUrl: "/images/location-pin.png",
    iconSize: [38, 38], // size of the icon
  })

  const createCustomClusterIcon = (cluster) => {
    return new divIcon({
      html: `<div class="cluster-icon">${cluster.getChildCount()}</div>`,
      className: "custom-marker-cluster",
      iconSize: point(33, 33, true)
    });
  };

  const [start, setStart] = useState("");
  const [end, setEnd] = useState("");
  const [startCoords, setStartCoords] = useState("");
  const [endCoords, setEndCoords] = useState("");
  const [routeCoords, setRouteCoords] = useState([]);

  const fetchCoordinates = async (place, setCoords) => {
    try{
      const res = await axios.get("https://api.opencagedata.com/geocode/v1/json", {
        params: {
          q: place,
          key: "ecf2d9c5db294d8eaf318a95ded1eacd",
        },
      });
      const { lat, lng } = res.data.results[0].geometry;
      setCoords([lat, lng]);
    } catch(err) {
      console.error("Error fetching coordinates: ", err);
    }
  };

  const handleSearch = () => {
    fetchCoordinates(start, setStartCoords);
    fetchCoordinates(end, setEndCoords);
  };

  const fetchRoute = async (startCoords, endCoords) => {
    if(!startCoords || !endCoords) return;
    try{
      const res = await axios.post(
        "https://api.openrouteservice.org/v2/directions/driving-car/geojson"
      )
    } catch (err) {
      console.error("Error fetching route: ", err);
    }
  }

  return(
    <div>
      <h2>Route Finder</h2>
      <input
        type='text'
        placeholder='Start location'
        value={start}
        onChange={(e) => setStart(e.target.value)}
      />
      <input
        type='text'
        placeholder='End location'
        value={end}
        onChange={(e) => setEnd(e.target.value)}
      />
      <button onClick={handleSearch}>Find</button>

      <MapContainer center={[48.8566,2.3522]} zoom={13}>
        {/* <TileLayer
          attribution = '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        /> */}

        <TileLayer
          attribution = 'Esri'
          url = 'https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png'
          // onClick={(e) => {
          //   console.log(e);
          // }}
        />

        {startCoords && (
          <Marker position={startCoords} icon={customIcon}>
            <Popup>Start: {start}</Popup>
          </Marker>
        )}
        {endCoords && (
          <Marker position={endCoords} icon={customIcon}>
            <Popup>End: {end}</Popup>
          </Marker>
        )}

        <MarkerClusterGroup
          chunkedLoading
          iconCreateFunction={createCustomClusterIcon}
        >
          {markers.map(marker => (
            <Marker position={marker.geocode} icon={customIcon}>
              <Popup>
                <h4>
                  {marker.popup}
                </h4>
              </Popup>
            </Marker>
          ))}
        </MarkerClusterGroup>
      </MapContainer>
    </div>


  );
}

export default App;
